
def longestPalindrome(s):

    longest = ''

    if len(s) == 1:
        return s

    for i in range(len(s)):
        for j in range(i+1,len(s)+1):
            if s[i:j] == s[j:i:-1] and len(s[i:j+1]) > len(longest):
                longest = s[i:j+1]

    if len(longest) == 0:
        return s[0]
    else:
        return longest
print(longestPalindrome(""))